import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const BookCard = (props) => (
  <div class="card-container">
    <img
      src="https://dashboard.imgix.com/assets/000000000000000000000000/scooter.jpg"
      alt="Books"
      height="200"
    />
    <div  class="desc">
      <h2>{props.book.title}</h2>
      <h3>{props.book.author}</h3>
      <p>{props.book.description}</p>
    </div>
    <button className="delete-btn" onClick={() => props.deleteBook(props.book._id)}>X</button>
  </div>
);

export default function BookList() {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:5000/book') 
      .then((res) => {
        setBooks(res.data);
      })
      .catch((err) => {
        console.log('Error from BookList', err);
      });
  }, []);

  const deleteBook = (id) => {
    axios
      .delete(`http://localhost:5000/book/delete/${id}`)
      .then((response) => {
        console.log(response.data);
      });

    setBooks(books.filter((el) => el._id !== id));
  };

  const bookList =
    books.length === 0 ? (
      <p>There are no book records!</p>
    ) : (
      <div className="list">
        {books.map((book) => (
              <BookCard book={book} key={book._id} deleteBook={deleteBook} />
        ))}
      </div>
    );

  return (
    <>
    <head>
    <link
      rel="stylesheet"
      href="https://maxcdn.bootstrapcdn.com/bootstrap/4.0.0/css/bootstrap.min.css"
      integrity="sha384-Gn5384xqQ1aoWXA+058RXPxPg6fy4IWvTNh0E263XmFcJlSAwiGgFAW/dAiS6JXm"
      crossorigin="anonymous"
    />
    <link rel="stylesheet" href="../styles.css" />
  
  </head>
    <div className="BookList">
      <div className="container">
        <div className="row">
          <div className="col-md-12">
            <br />
            <h2 className="display-4 text-center">Books in the List</h2>
          </div>
          <div className="col-md-12">
            <br />
            <h2 className="counter"> <p>{books.length}</p></h2>
          </div>

          <div className="col-md-11">
            <Link
              to="/create-book"
              className="btn btn-outline-warning float-right"
            >
              + Add New Book
            </Link>
            <br />
            <br />
            <hr />
          </div>
        </div>

        <div className="list">{bookList}</div>
      </div>
    </div>
    </>
  );
}
